import React, { useEffect, useState } from "react";

const Tokens = () => {
  const [cryptoData, setCryptoData] = useState([]);

  useEffect(() => {
    const fetchCryptoData = async () => {
      try {
        const response = await fetch(
          "https://api.coingecko.com/api/v3/simple/price?ids=chainlink,polygon,uniswap,curve-dao-token,maker,bitcoin,apecoin,usd-coin,shiba-inu,sushiswap,okb&vs_currencies=usd&include_24hr_change=true"
        );
        const data = await response.json();

        const selectedTokens = ["LINK", "WBTC", "WETH", "MATIC", "SHIB", "UNI", "OKB", "APE", "SUSHI", "USDC"];

        const updatedCryptoData = selectedTokens.map((token) => {
          const tokenData = data[token.toLowerCase()];
          if (tokenData) {
            const price = tokenData.usd || 0;
            const change24h = tokenData.usd_24h_change || 0;
            return { token, price, change24h };
          }
          return { token, price: 0, change24h: 0 };
        });

        setCryptoData(updatedCryptoData);
      } catch (error) {
        console.log("Error fetching crypto data:", error);
      }
    };

    fetchCryptoData();
  }, []);

  return (
    <div className="tokens_box">
      <div className="tokens_box_header">Token price & change</div>
      <div className="tokens_box_content">
        <div className="tokens_column">
          <h4 className="tokens_column_header">Tokens</h4>
          {cryptoData.map((crypto) => (
            <div className="token_entry" key={crypto.token}>
              <p className="token_name">{crypto.token}</p>
            </div>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">Price</h4>
          {cryptoData.map((crypto) => (
            <div className="token_entry" key={crypto.token}>
              <p className="token_price">${crypto.price.toFixed(2)}</p>
            </div>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">24h Change</h4>
          {cryptoData.map((crypto) => (
            <div className="token_entry" key={crypto.token}>
              <p className="token_change">{crypto.change24h.toFixed(2)}%</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tokens;
