import React, { useEffect, useState } from "react";

const Tokens = () => {
  const [cryptoData, setCryptoData] = useState([]);

  useEffect(() => {
    const fetchCryptoData = async () => {
      try {
        const response = await fetch(
          "https://api.coingecko.com/api/v3/simple/price?ids=chainlink,polygon,uniswap,curve-dao-token,maker&vs_currencies=usd&include_24hr_change=true&include_7d_change=true&include_30d_change=true"
        );
        const data = await response.json();

        // Обработка данных
        const tokens = Object.keys(data);
        const updatedCryptoData = tokens.map((token) => {
          const price = data[token].usd;
          const change24h = data[token].usd_24h_change;
          const change7d = data[token].usd_7d_change;
          const change30d = data[token].usd_30d_change;
          return { token, price, change24h, change7d, change30d };
        });

        setCryptoData(updatedCryptoData);
      } catch (error) {
        console.log("Error fetching crypto data:", error);
      }
    };

    fetchCryptoData();
  }, []);

  return (
    <div className="tokens_box">
      <div className="tokens_box_header">Token price & change</div>
      <div className="tokens_box_content">
        <div className="tokens_column">
          <h4 className="tokens_column_header">Tokens</h4>
          {cryptoData.map((crypto) => (
            <p className="token_name" key={crypto.token}>
              {crypto.token}
            </p>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">24h Change</h4>
          {cryptoData.map((crypto) => (
            <p className="token_change" key={crypto.token}>
              {crypto.change24h.toFixed(2)}%
            </p>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">7d Change</h4>
          {cryptoData.map((crypto) => (
            <p className="token_change" key={crypto.token}>
              {crypto.change7d.toFixed(2)}%
            </p>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">30d Change</h4>
          {cryptoData.map((crypto) => (
            <p className="token_change" key={crypto.token}>
              {crypto.change30d.toFixed(2)}%
            </p>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tokens;
