import React, { useEffect, useState } from "react";

const Tokens = () => {
  const [cryptoData, setCryptoData] = useState([]);

  useEffect(() => {
    const fetchCryptoData = async () => {
      try {
        const response = await fetch(
          "https://api.coingecko.com/api/v3/simple/price?ids=chainlink,polygon,uniswap,curve-dao-token,maker,bitcoin,apecoin,usd-coin,shiba-inu,ethereum,binancecoin,cardano,ripple,litecoin,tezos,stellar,eos,vechain,dogecoin,tron,celsius,algorand&vs_currencies=usd&include_24hr_change=true"
        );
        const data = await response.json();

        const tokens = Object.keys(data);
        const updatedCryptoData = tokens.map((token) => {
          const price = data[token]?.usd || 0;
          const change24h = data[token]?.usd_24h_change || 0;
          return { token, price, change24h };
        });

        setCryptoData(updatedCryptoData);
      } catch (error) {
        console.log("Error fetching crypto data:", error);
      }
    };

    fetchCryptoData();
  }, []);

  // Обновленный список токенов с указанием полного имени
  const tokenList = [
    { ticker: "LINK", name: "Chainlink" },
    { ticker: "WBTC", name: "Wrapped Bitcoin" },
    { ticker: "WETH", name: "Wrapped Ethereum" },
    { ticker: "MATIC", name: "Matic Token" },
    { ticker: "SHIB", name: "Shiba Inu" },
    { ticker: "UNI", name: "Uniswap" },
    { ticker: "OKB", name: "OKB" },
    { ticker: "APE", name: "Ape Coin" },
    { ticker: "SUSHI", name: "SushiToken" },
    { ticker: "USDC", name: "USD Coin" },
    { ticker: "CELSIUS", name: "Celsius" },
  ];

  return (
    <div className="tokens_box">
      <div className="tokens_box_header">Token price & change</div>
      <div className="tokens_box_content">
        <div className="tokens_column">
          <h4 className="tokens_column_header">Tokens</h4>
          {tokenList.map((token) => (
            <div className="token_entry" key={token.ticker}>
              <p className="token_name">{token.name}</p>
            </div>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">Price</h4>
          {tokenList.map((token) => (
            <div className="token_entry" key={token.ticker}>
              <p className="token_price">
                ${cryptoData.find((crypto) => crypto.token === token.ticker)?.price.toFixed(2)}
              </p>
            </div>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">24h Change</h4>
          {tokenList.map((token) => (
<div className="token_entry" key={token.ticker}>
<p className="token_change">
{cryptoData.find((crypto) => crypto.token === token.ticker)?.change24h.toFixed(2)}%
</p>
</div>
))}
</div>
</div>
</div>
);
};

export default Tokens;
