import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";

const Tokens = () => {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchCryptoData = async () => {
      try {
        const response = await fetch(
          "https://api.coingecko.com/api/v3/simple/price?ids=chainlink,polygon,uniswap,curve-dao-token,maker,shiba-inu&vs_currencies=usd"
        );
        const data = await response.json();

        const chartData = {
          labels: ["LINK", "MATIC", "UNI", "CRV", "MKR", "SHIB"],
          datasets: [
            {
              label: "Crypto Prices",
              data: [
                data.chainlink.usd,
                data.polygon.usd,
                data.uniswap.usd,
                data["curve-dao-token"].usd,
                data.maker.usd,
                data["shiba-inu"].usd,
              ],
              backgroundColor: "rgba(75, 192, 192, 0.6)",
            },
          ],
        };

        setChartData(chartData);
      } catch (error) {
        console.log("Error fetching crypto data:", error);
      }
    };

    fetchCryptoData();
  }, []);

  return (
    <div>
      {chartData && (
        <Bar
          data={chartData}
          options={{
            responsive: true,
            maintainAspectRatio: false,
          }}
        />
      )}
    </div>
  );
};

export default Tokens;
