import React, {useState, useEffect} from 'react'
import {Input, Popover, Radio, Modal, message} from "antd"
import {
  ArrowDownOutlined, 
  DownOutlined,
  SettingOutlined,
} from "@ant-design/icons"
import tokenList from "../tokenList.json"
import axios from "axios"
import { useSendTransaction, useWaitForTransaction} from "wagmi"
import GasTracker from './GasTracker'

function Swap(props) {
  const { address, isConnected } = props;
  const [message_api, contextHolder] = message.useMessage();
  const [slippage, set_slippage] =useState(2.5);
  const [token_one_amount, set_token_one_amount] =useState (null);
  const [token_two_amount, set_token_two_amount] =useState (null);
  
  const [isOpen, setIsOpen] = useState(false);
  const [changeToken, setChangeToken] = useState(1);
  const [prices, setPrices] = useState(null);
  const [txDetails, setTxDetails] = useState({
    to: null,
    data: null,
    value: null,
  });

  const {data, sendTransaction} = useSendTransaction({
    request: {
      from: address,
      to: String(txDetails.to),
      data: String(txDetails.data),
      value: String(txDetails.value),
    }
  })

  const { isLoading, isSuccess } = useWaitForTransaction({
    hash: data?.hash,
  })


  function handleSlippageChange(e) {
    set_slippage(e.target.value);
  }


  function changeAmount(e) {
    set_token_one_amount(e.target.value);
    if(e.target.value && prices) {
      set_token_two_amount((e.target.value * prices.ratio).toFixed(5))
    } else {
      set_token_two_amount(null);
    }
  }

  function switchTokens() {
    setPrices(null);
    set_token_one_amount(null);
    set_token_two_amount(null);
    const one = token_one;
    const two = token_two;
    set_token_one(two);
    set_token_two(one);
    fetchPrices(two.address, one.address);
  }

  function openModal(asset) {
    setChangeToken(asset);
    setIsOpen(true);
  }

  function modifyToken(i) {

    setPrices(null);
    set_token_one_amount(null);
    set_token_two_amount(null);

    if (changeToken === 1) {
      set_token_one (tokenList[i]);
      fetchPrices(tokenList[i].address, token_two.address)
    } else {
      set_token_two(tokenList[i]);
      fetchPrices(token_one.address, tokenList[i].address)
    }
    setIsOpen(false);
  }

  async function fetchPrices(one, two) {

    const res = await axios.get('http://localhost:3001/tokenPrice', {
      params: {addressOne: one, addressTwo: two}
    })
    
    setPrices(res.data)
  }

  async function fetchDexSwap() {
     const allowance = await axios.get(`https://api.1inch.io/v5.0/1/approve/allowance?tokenAddress=${token_one.address}&walletAddress=${address}`)
    
    if(allowance.data.allowance === "0") {

      const approve = await axios.get(`https://api.1inch.io/v5.0/1/approve/transaction?tokenAddress=${token_one.address}`)

      setTxDetails(approve.data);
      console.log("not approved")
      return
    }

    const tx = await axios.get(
      `https://api.1inch.io/v5.0/1/swap?fromTokenAddress=${token_one.address}&toTokenAddress=${token_two.address}&amount=${token_one_amount.padEnd(token_one.decimals+token_one_amount.length, '0')}&fromAddress=${address}&slippage=${slippage}`
    )

      let decimals = Number(`1E${token_two.decimals}`)
      set_token_two_amount((Number(tx.data.toTokenAmount)/decimals).toFixed(5));

      setTxDetails(tx.data.tx);
      
    }

  useEffect(()=>{

    fetchPrices(tokenList[0].address, tokenList[1].address)

  }, [])


  useEffect (()=>{

    if(txDetails.to && isConnected){
      sendTransaction();
    }
  }, [txDetails])

  useEffect (()=>{

    message_api.destroy();

    if(isLoading){
      message_api.open({
        type:'loading',
        content: 'Transaction is Pending...',
        duration: 0,
      })
    }

  },[isLoading])


  useEffect (()=>{

    message_api.destroy();

    if(isSuccess){
      message_api.open({
        type:'success',
        content: 'Transaction Successful',
        duration: 1.5,
      })
    }else if(txDetails.to){
      message_api.open({
        type:'error',
        content: 'Transaction Failed',
        duration: 1.5,
      })
    }

  }, [isSuccess])


  const settings = (
    <>
      <div>Slippage tolerance</div>
      <div>
        <Radio.Group value={slippage} onChange={handleSlippageChange}>
          <Radio.Button value={0.5}>0.5%</Radio.Button>
          <Radio.Button value={2.5}>2.5%</Radio.Button>
          <Radio.Button value={5}>5.0%</Radio.Button>
        </Radio.Group>
      </div>


    </>
  );

  return (
    <>
    {contextHolder}
    <Modal
      open={isOpen}
      footer={null}
      onCancel={() => setIsOpen(false)}
      title="Select a token"
    > 
      <div className='modalContent'>
        {tokenList?.map((e,i)=>{
          return (
            <div
              className='tokenChoise'
              key={i}
              onClick={() => modifyToken(i)} 
            >
            <img src={e.img} alt={e.ticker} className='tokenLogo'/>
            <div className='tokenChoiseNames'>
                <div className='tokenName'>{e.name}</div>
                <div className='tokenTicker'>{e.ticker}</div>
            </div>
        </div>
          );
        })}
      </div>
    </Modal>
    <div className='tradeBox'>
      <div className='tradeBoxHeader'>
        <h4 className='tradeBoxHeaderText'>Exchange tokens</h4>
        <Popover 
          content = {settings}
          title="Settings"
          trigger='click'
          placement='bottomRight'
          >
          <SettingOutlined className='cog' /> 
        </Popover>
      </div>
      <div className='inputs'>
        <Input 
        placeholder='0' 
        value={token_one_amount} 
        onChange={changeAmount}
        disabled={!prices} 
        />
        <Input placeholder='0' value={token_two_amount} disabled={true} />
        <div className='switchButton' onClick={switchTokens}>
          <ArrowDownOutlined className='switchArrow'/>
        </div>
        <div className='assetOne' onClick={() => openModal(1)}>
          <img src={token_one.img} alt='assetOneLogo' className='assetLogo'/>
          {token_one.ticker}
          <DownOutlined />
        </div>
        <div className='assetTwo' onClick={() => openModal(2)}>
        <img src={token_two.img} alt='assetTwoLogo' className='assetLogo'/>
          {token_two.ticker}
          <DownOutlined />
        </div>
      </div>
      <div className='swapButton' disabled={!token_one_amount || !isConnected} onClick={fetchDexSwap}>Swap</div>
      <GasTracker></GasTracker>
    </div>
    </>
  )
}

export default Swap
