import React, { useEffect, useState } from "react";

const Tokens = () => {
  const [cryptoData, setCryptoData] = useState([]);

  useEffect(() => {
    const fetchCryptoData = async () => {
      try {
        const response = await fetch(
          "https://api.coingecko.com/api/v3/simple/price?ids=chainlink,polygon,uniswap,curve-dao-token,maker,bitcoin,apecoin,usd-coin,shiba-inu,ethereum,binancecoin,cardano,ripple,litecoin,tezos,stellar,eos,vechain,dogecoin,tron,celsius,algorand&vs_currencies=usd&include_24hr_change=true"
        );
        const data = await response.json();

        const tokens = Object.keys(data);
        const updatedCryptoData = tokens.map((token) => {
          const price = data[token]?.usd || 0;
          const change24h = data[token]?.usd_24h_change || 0;
          const weekChange = 0; // Изначально установлено значение "Week Change" равным 0
          return { token, price, change24h, weekChange };
        });

        setCryptoData(updatedCryptoData);
      } catch (error) {
        console.log("Error fetching crypto data:", error);
      }
    };

    fetchCryptoData();
  }, []);

  useEffect(() => {
    const fetchWeekChange = async () => {
      try {
        const response = await fetch(
          "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=chainlink,polygon,uniswap,curve-dao-token,maker,bitcoin,apecoin,usd-coin,shiba-inu,ethereum,binancecoin,cardano,ripple,litecoin,tezos,stellar,eos,vechain,dogecoin,tron,celsius,algorand&sparkline=true"
        );
        const data = await response.json();

        const updatedCryptoData = cryptoData.map((crypto) => {
          const weekChangeData = data.find((item) => item.id === crypto.token);
          const weekChange = weekChangeData?.sparkline_in_7d?.price || [];
          const currentPrice = crypto.price;
          const weekChangePercentage =
            ((currentPrice - weekChange[0]) / weekChange[0]) * 100 || 0;
          return { ...crypto, weekChange: weekChangePercentage };
        });

        setCryptoData(updatedCryptoData);
      } catch (error) {
        console.log("Error fetching week change data:", error);
      }
    };

    fetchWeekChange();
  }, [cryptoData]);

  return (
    <div className="tokens_box">
      <div className="tokens_box_header">Token price & change</div>
      <div className="tokens_box_content">
        <div className="tokens_column">
          <h4 className="tokens_column_header">Tokens</h4>
          {cryptoData.map((crypto) => (
            <div className="token_entry" key={crypto.token}>
              <p className="token_name">{crypto.token}</p>
            </div>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">Price</h4>
          {cryptoData.map((crypto) => (
            <div className="token_entry" key={crypto.token}>
              <p className="token_price">${crypto.price.toFixed(2)}</p>
            </div>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">24h Change</h4>
          {cryptoData.map((crypto) => (
            <div className="token_entry" key={crypto.token}>
              <p className="token_change">{crypto.change24h.toFixed(2)}%</p>
            </div>
          ))}
        </div>
        <div className="tokens_column">
          <h4 className="tokens_column_header">Week Change</h4>
          {cryptoData.map((crypto) => (
            <div className="token_entry" key={crypto.token}>
              <p className="token_week_change">
                {crypto.weekChange.toFixed(2)}%
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tokens;
